/* ==UserStyle==
@name           Magnolia
@namespace      github.com/openstyles/stylus
@version        1.8.7
==/UserStyle== */
@-moz-document regexp(".*mwcm-author.*/.magnolia.*"),
regexp(".*localhost:8080.*magnolia.*")
{
    :root
    {
        --foreground-color: #666;
        --background-color: #e0e0e0;
        --dark-grey: #666;
        --light-grey: #e0e0e0;
    }

    iframe
    {
        font-family: sans-serif;
    }

    /* Body copy */
    p
    {
        font-family: nyt-franklin, franklin, sans-serif;
    }

    /* "Favorites" button */
    .btn-shell .badge-icon,
    .v-shell-notification .details-expander,
    .v-shell-tabsheet-logo,
    .v-sticker-environment,
    /* [class*='icon-']::before, */
    #btn-favorites,
    #footer,
    #login .header,
    #login-divet,
    #main-launcher #divet,
    #main-launcher #logo,
    #z,
    /*.v-shell-tabsheet .tab-title,*/
    .v-shell-tabsheet .single-tab
    {
        display: none;
    }

    body.v-generated-body
    {
        background: #eee;
    }

    #main-launcher
    {
        background-color: #e0e0e0;
        border-bottom: 1px solid #c1c1c1;
        color: var(--foreground-color);
        text-align: left;
    }

    /* Primary buttons (icons and labels) "Apps", "Pages" etc */
    .btn-shell
    {
        /*  width: auto; */
        font-size: 22px;
        padding-left: 18px;
        padding-right: 18px;
    }

    .btn-shell::after
    {
        display: block;
        font-family: sans-serif;
    }

    /* current path */
    .v-horizontallayout-pageeditor .pagebar
    {
        background-color: var(--background-color);
        color: var(--foreground-color) !important;
    }

    .v-horizontallayout-pageeditor .pagebar > .page-link a::before
    {
        color: var(--foreground-color) !important;
    }

    .v-horizontallayout-pageeditor .pagebar > .page-link,
    .v-horizontallayout-pageeditor .pagebar > .page-link a,
    .v-horizontallayout-pageeditor .pagebar > .page-link a::before,
    .v-horizontallayout-pageeditor .pagebar .page-link.fastly a::before
    {
        font-family: nyt-franklin, franklin, sans-serif;
        font-size: 16px;
    }

    .v-horizontallayout-pageeditor .pagebar > .v-label
    {
        font-family: nyt-franklin, franklin, sans-serif;
        font-size: 16px;
    }

    .v-shell-user-menu-wrapper
    {
        right: 0;
        top: 0;
    }

    /* Button Icons */
    .icon-appslauncher::before,
    .icon-pulse::before,
    .icon-pages::before,
    .icon-assets::before,
    .icon-favorites::before,
    .icon-creatives::before
    {
        display: none;
        font-size: 22px;
    }

    /* Button Text labels */
    /* Add text */
    .icon-appslauncher::after
    {
        content: 'Apps';
    }

    .icon-pulse::after
    {
        content: 'Tasks';
    }

    .icon-favorites::after
    {
        content: 'Favorites';
    }

    .icon-creatives::after
    {
        content: 'Creatives';
    }

    .icon-appslauncher::after,
    .icon-pulse::after,
    .icon-favorites::after,
    .icon-pages::after,
    .icon-assets::after,
    .icon-creatives::after
    {
        font-family: DINWebPro, Verdana, sans-serif;
        font-size: 18px;
        left: 50%;
        position: absolute;
        text-align: center;
        transform: translateX(-50%);
    }

    /* Button badges */
    .btn-shell .badge-icon
    {
        background-color: var(--light-grey) !important;
        border-color: var(--dark-grey) !important;
        color: var(--dark-grey) !important;
        z-index: 187;
    }

    /* Badges inside "Tasks" Menu */
    .v-pulse .navigator-tab .badge
    {
        background-color: var(--dark-grey);
    }

    /* "View preview in new window" link */
    .v-horizontallayout-pageeditor .pagebar > .page-link
    {
        font-size: 100%;
        font-weight: bold;
    }

    .v-horizontallayout-pageeditor .pagebar .page-link a
    {
        /*  position: relative; */
    }

    .v-horizontallayout-pageeditor .pagebar .page-link a::before
    {
        color: #efefef;
        content: 'Preview';
        font-family: DINWebPro, Verdana, sans-serif;
        font-size: 12px;
        line-height: 28px;
    }

    .v-horizontallayout-pageeditor .pagebar .page-link.fastly a::before
    {
        color: #efefef;
        content: 'Preview';
        font-family: DINWebPro, Verdana, sans-serif;
        font-size: 12px;
        line-height: 28px;
    }

    .v-horizontallayout-pageeditor .pagebar > .page-link > a
    {
        left: auto;
        position: relative;
    }

    /* Primary Magnolia logo */
    #main-launcher
    {
        height: 35px;
    }

    .v-shell-viewport-slot
    {
        top: 35px;
    }

    /* Transitions on app launcher and viewport */
    #main-launcher,
    .v-viewport
    {
        /* transition: none !important; */
        /* transition-duration: 0 !important; */
    }

    /* Custom pages button */
    .icon-pages:before
    {
        content: '\e004';
    }

    .icon-pages::after
    {
        content: 'Pages';
    }

    /* Custom assets button */
    .icon-assets:before
    {
        content: '\e00b';
    }

    .icon-assets::after
    {
        content: 'Assets';
    }

    /* Custom creatives button */
    .icon-creatives:before
    {
        content: '\e00d';
    }

    .icon-creatives::after
    {
        content: 'Creatives';
    }

    /* Custom buttons */
    .link-btn
    {
        color: #666;
    }

    /* Sticker indicating which environment we're in */
    .v-shell-user-menu-wrapper .v-sticker
    {
        background-color: #666 !important;
        color: #e0e0e0;
        font-weight: normal;
    }

    .v-horizontallayout-browser .v-verticallayout-workbench
    {
        padding-left: 0;
    }

    /* Title of sidebar */
    .v-actionbar .v-actionbar-section-title
    {
        color: #fff;
    }

    /* Nav items under Apps */
    .item
    {
        background-color: #333;
    }

    /* Notification bar */
    /* Prevent bar from covering save button in modal dialogs */
    .v-shell-notification
    {
        padding: 0 10px;
        width: auto;
        width: 300px;
    }

    .v-shell-notification .header,
    .v-shell-message-detail
    {
        height: auto;
    }

    .v-shell-notification .close
    {
        cursor: pointer;
    }

    /* Restore text-selection cursor */
    .v-app,
    .v-window,
    .v-popupview-popup,
    .v-label,
    .v-caption
    {
        cursor: text;
    }

    /* Login page */
    body,
    #login .mgnl-form
    {
        background-color: var(--background-color);
        background-image: none;
    }

    body.theme-
    {
        font-family: sans-serif;
        margin: 0;
    }

    /* Link to creative */
    body.theme- .page__content
    {
        font-family: sans-serif;
    }

    /* Creative name */
    .v-horizontallayout-pageeditor .pagebar > .v-label
    {
        color: #000;
        font-size: 18px;
        margin-left: 0;
    }

    #loginForm label
    {
        color: #333;
        font-size: 14px;
        letter-spacing: 0;
    }

    .icon-appslauncher:before
    {
        content: '\e904';
    }

    .v-pulse .task .comment
    {
        color: #000;
        font-size: 14px;
        top: 16px;
    }

    .v-pulse .v-grid-row-selected .v-grid-cell .comment
    {
        color: #fff;
    }

    .v-pulse .v-grid-row-selected .v-grid-cell
    {
        background-color: #666;
        color: #fff;
    }

    /* inline editor dialog */
    .ace_content
    {
        font-family: 'Fira Code', 'Consolas', 'Inconsolata-Regular', 'Courier-New', monospace !important;
    }

    /* Green overlay */
    .v-curtain,
    .v-curtain-green
    {
        background-color: #666;
        border-width: 0;
    }

    .form-content
    {
        transition: none;
        transition-duration: 0;
    }

    /* Dialogs */
    .light-dialog-panel.msgwarn
    {
        background: #eee;
        border-color: #666;
    }

    .overlay.open .light-dialog-panel.confirmation,
    .overlay.open .light-dialog-panel.alert
    {
        -webkit-animation-duration: 0;
        -moz-animation-duration: 0;
        -ms-animation-duration: 0;
        -o-animation-duration: 0;
        animation-duration: 0;
        -webkit-animation-name: none;
        -moz-animation-name: none;
        -ms-animation-name: none;
        -o-animation-name: none;
        animation-name: none;
    }

    .light-dialog-panel
    {
        border: solid 1px #d1d1d1;
        box-shadow: none;
    }

    /* buttons */
    .v-button.commit,
    .v-button.v-button-commit,
    .v-button.v-button-submit,
    .v-nativebutton-magnoliabutton.commit,
    .v-nativebutton-magnoliabutton.v-button-commit,
    .v-nativebutton-magnoliabutton.v-button-submit,
    button.btn-dialog.commit,
    button.btn-dialog.v-button-commit,
    button.btn-dialog.v-button-submit
    {
        background-color: black;
    }

    .dialog-header,
    .dialog-header span
    {
        color: white;
        font-family: franklin, nyt-franklin, sans-serif;
        font-size: 16px;
    }

    .v-form-field-label
    {
        color: black;
    }

    .v-form-field-label,
    .validation-message-inline,
    .help-message-inline,
    .help-bubble-inline,
    .info-bubble-inline,
    .warning-bubble-inline,
    .error-bubble-inline,
    .v-captiontext
    {
        font-size: 14px;
        color: black;
    }

    button.btn-form-wide
    {
        color: white;
    }

    .btn-form-help
    {
        display: none
    }

    .icon-status-green:before
    {
        font-family: "MagnoliaIcons" !important;
        content: "✓";
    }

    .icon-status-red:before
    {
        content: "✕";
    }

    .icon-status-orange:before
    {
        content: "*";
        font-size: 28px;
        font-weight: 600;
        color: goldenrod;
    }

    /* Hide unused apps */
    .app-list:nth-of-type(1) > div.item:nth-of-type(5),
    .app-list:nth-of-type(1) > div.item:nth-of-type(6),
    .app-list:nth-of-type(1) > div.item:nth-of-type(7),
    .app-list:nth-of-type(3) > div.item:nth-of-type(7),
    .app-list:nth-of-type(3) > div.item:nth-of-type(8),
    .app-list:nth-of-type(2) > div.item:nth-of-type(1),
    .app-list:nth-of-type(2) > div.item:nth-of-type(2),
    .app-list:nth-of-type(2) > div.item:nth-of-type(3),
    .app-list:nth-of-type(4),
    .app-list:nth-of-type(5)
    {
        display: none !important;
    }

    .v-shell-tabsheet-tab-wrapper
    {
        padding-bottom: 10px !important;
    }

    .v-app
    {
        font-family: 'San Francisco', sans-serif !important;
        font-size: 13px;
    }

    /* Editor warning message*/
    .editMode__message--warn
    {
        border: none !important;
        background-color: #ccc !important;
    }

    .editMode__message--warn:before
    {
        display: none;
    }

    .mgnlEditMode .productGBB__button__wrap
    {
        text-align: left;
    }

    div.mgnlEditorBarButtons
    {
        padding-right: 40px !important;
    }

    /* Edit Page Properties dialog */
    [title*="HTML Title"],
    [title*="HTML Title"] + .v-form-field-container,
    [title*="JIRA ticket"],
    [title*="Abra Tests"] + .v-form-field-container,
    [title*="Abra Tests"],
    [title*="JIRA ticket"] + .v-form-field-container,
    [title*="AMP Mobile URL"],
    [title*="AMP Mobile URL"] + .v-form-field-container
    {
        display: none !important;
    }

    /* Make text selectable */
    body
    {
        -webkit-user-select: auto !important;
        -webkit-tap-highlight-color: unset !important;
        -webkit-touch-callout: unset !important;
    }

    #subscriptionSearchZip_form
    {
        display: block !important;
    }

