    {
        color: #333;
        font-size: 14px;
        letter-spacing: 0;
    }

    /* Publish status indicators*/
    .icon-status-green:before
    {
        content: "\2713";
    }

    .icon-status-orange:before
    {
        content: "\02731";
    }

    .icon-status-red:before
    {
        content: "\2715";
    }
}

