/* ==UserStyle==
@name           Emporium
@namespace      github.com/openstyles/stylus
@version        1.8.7
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("empornium.me")
{
    [href*="/feeds.php?"],
    [src*="/static/common/icons/cast11.png"],
    [src*="/static/common/icons/info11.png"],
    [src*="/static/common/icons/details11.png"],
    .user_name,
    [href="#quickpost"]
    {
        display: none !important;
    }
    table.forum_post
    {
        font-size: 16px;
    }
    h2
    {
        margin: 10px 0px 20px;
        font-weight: bold;
        color: black;
        line-height: 33px;
        padding: 10px;
        border: 1px solid #c7c7c7 !important;
        background-color: #f8f8f8;
        background-image: none;
        filter: none;
        box-shadow: none;
        font-size: 1.1em !important;
        border-radius: 0;
        text-align: left;
        /* overflow: hidden; */
    }
    #header
    {
        width: 100%;
        min-width: 1000px;
        padding: 0;
        background-color: #0074b7;
        background-image: none;
        font-weight: normal;
    }
    /* Hide logo */
    #logo img,
    #logo,
    #nav_donate,
    #nav_conncheck,
    #thanksdiv,
    #torrents .forum_post,
    #quickpostform,
    #torrentsigbox,
    [src*="/static/common/icons/"],
    [src*="/images/2019/04/03/image.png"],
    [style*="/images/2019/04/02/458773169.png"],
    .arrow,
    #thanksform
    {
        display: none !important;
    }
    /* Tame alerts */
    .alertbar
    {
        background-color: transparent;
        border-radius: 0;
        text-align: left;
    }

    #details-sidebar
    {
        display: none;
    }

    #header
    {
        background: none transparent;
        background-color: black;
        color: #333 !important;
    }
    #searchbars
    {
        text-align: left;
        height: auto;
    }
    #searchbars .searchcontainer
    {
        border-radius: 0;
        background-color: transparent;
        border-width: 0 0 2px 0;
        border-color: white;
    }
    #searchbars input.searchbox
    {
        border-radius: 0;
        background: transparent;
        color: #fff;
    }
    #searchbars input.searchbox
    {
        font-size: 20px;
        line-height: 1.4;
        margin: 5%;
    }
    #menu
    {
        margin: 0;
        text-align: left;
    }
    #header_bottom
    {
        background-color: #333;
    }
    #nav_donate,
    #searchbars ul #searchbar_users
    {
        display: none;
    }
    #footer
    {
        display: none;
    }

    a:visited,
    .linkbox a:visited,
    .permission_container a:visited,
    .forum_index a:visited,
    .forum_list a:visited,
    #search_button:visited,
    #request_table a:visited,
    .cat_list a:visited,
    .tags a:visited,
    .head a:visited,
    .colhead_dark a:visited
    {
        color: black;
    }
    div.linkbox,
    .details div.linkbox,
    h2
    {
        text-align: left;
    }
    span
    {
        float: none !important;
    }

    .head,
    h2,
    .top_info
    {
        border-width: 0;
        background-image: none;
        background-color: #fff;
        box-shadow: none;
        border-radius: 0;
    }
    a:visited,
    .linkbox a:visited,
    .permission_container a:visited,
    .forum_index a:visited,
    .forum_list a:visited,
    #search_button:visited,
    #request_table a:visited,
    .cat_list a:visited,
    .tags a:visited,
    .head a:visited,
    .colhead_dark a:visited
    {
        color: #aaa;
    }
    #descbox
    {
        font-size: 18px;
        font-family: sans-serif;
    }
}

