    background-color: transparent;
    border-radius: 0;
    box-shadow: none;
}
.ProfileTemplate,
.ProfileTemplate.m-updated
{
    background-color: transparent;
}
.Post .m-notProfileOwner
{
    border-width: 0;
    border-style: none;
    margin-left: 0;
    margin-right: 0;
}
.Post.m-profile
{
    border-width: 0;
    border-style: none;
}
.Post__origin
{
    display: none;
}
.PostList p
{
    font-size: 14px;
    color: #222;
}
.Post__titleLink
{
    color: #0000ff;
    font-size: 16px;
}
.Post__scoreDisplay.m-profile
{
    margin-right: 7px;
    border-width: 0;
}
.ProfileTemplate .Post__thumbnail
{
    margin-right: 7px;
    min-width: auto;
}
.ProfileTemplate .Post__score
{
    font-size: 13px;
}
.ProfileTemplate .Post__scoreWrap,
.ProfileTemplate .Post .m-notProfileOwner .Post__scoreWrap
{
    opacity: 1;
}
.ProfileTemplate .Post__top
{
    padding-left: 0;
    padding-right: 0;
}
.ProfileVoiceAd,
.TrendingProfilesSidebar,
.ProfileSidebar__usericon,
.ProfileBanner
{
    display: none;
}
.ProfileTemplate__sidebar
{
    margin-top: 0;
}
.ProfileSidebar
{
    top: auto;
}
.ProfileSidebar__nameTitleLink
{
    margin: 0px;
    margin-bottom: 5px;
    font-weight: bold;
    font-size: 19px;
}
.ProfileSidebar__counter.ProfileSidebar__karma
{
    font-size: 18px;
    color: #222;
}
.ProfileTemplate .Post__subredditLink
{
