/* ==UserStyle==
@name           mta.info
@namespace      github.com/openstyles/stylus
@version        1.8.7
==/UserStyle== */
@-moz-document domain("mta.info")
{
    #horizontalcssmenu ul#cssmenu1 li a
    {
        display: block;
        height: 16px;
        background: none;
        padding: 8px 14px;
        text-decoration: none;

        font-weight: bold;
        text-align: center;
        color: black;
    }

    #horizontalcssmenu ul#cssmenu1 li a:hover
    {
        background: none;
    }
    [bgcolor="eeeeff"],
    .smallerNavy,
    .smallerNavy a
    {
        background-color: #666;
        color: white !important;
        font-size: 16px
    }
    .smallerNavy a
    {
        text-decoration: underline;
    }
    button,
    input[type="reset"],
    input[type="submit"],
    input[type="button"]
    {
        -webkit-appearance: none;
        -moz-border-radius: 0;
        -webkit-border-radius: 0;
        border-radius: 0;
        background: none;
        background-color: #eee;
        border: 1px solid;
        border-color: transparent;
        cursor: pointer;
        color: #333;
        font-size: 16px;
        font-weight: bold;
        text-decoration: underline;
        outline: 0;
        overflow: visible;
        padding: 3px 10px 4px;
        text-shadow: #fff 0 1px 1px;
        width: auto;
    }
    [style*="color: #0000ff"]
    {
        color: #333!important;
    }
    [style*="font-size: small"]
    {
        font-size: 20px!important;
    }
    [style*='margin-left: 30px']
    {
        margin-left: 0 !important;
        padding-left: 10px !important;
        border-left: 1px solid #999;
    }
    a
    {
        text-decoration: underline !important;
    }
    #navyheader
    {
        padding: 0!important;
        margin: 0!important;
    }

    [style="padding-left:25px;"]
    {
        padding: 0!important;
    }
    #mainbox,
    #contentbox
    {
        width: auto!important;
    }
    .roundCorners
    {
        border: none!important;
        -moz-border-radius: 0!important;
        -webkit-border-radius: 0!important;
        border-radius: 0!important;
        background: #fff;
    }
    #contentbox h1,
    h1,
    h3
    {
        color: #333;
        font-size: 24px;
        display: block;
        padding-bottom: 0;
        border-bottom: none;
        margin-top: 1em;
        margin-bottom: .1em;
        font-weight: normal;
    }
    #contentbox ul.arrow li a
    {
        font-weight: normal;
    }
    #contentbox ul.arrow li,
    #contentbox ul.arrow li ul li,
    #contentbox #sidemenu li
    {
        list-style-type: none;
        margin: 0;
        padding-left: 0px;
        margin-bottom: 0;
        background-image: none;
        background-repeat: no-repeat;
        background-position: top left;
    }
    tr th,
    tr td
    {
        padding-top: 4px;
        padding-bottom: 2px;
        padding-left: 0px;
        padding-right: 2px;
    }
}

