@-moz-document domain("nyt-brand.monday.com")
{
    .image-title,
    .user_guidance_wrapper,
    .new-team-member-card-component,
    .end-of-year-feature-button-connector-wrapper,
    .new-boards-list-button-component .new-boards-list-button,
    .icon-v2-surface-invite,
    .icon-v2-my-week-o,
    .icon-v2-surface-help,
    .dashboards,
    .surface-avatar-menu-component .avatar-photo-button-wrapper .avatar-photo-dot.red-dot,
    .activity-log-button,
    .add-board-entity-button-wrapper,
    .automations-button-component,
