    .separator,
    .lottie-card-users-avatar-component,
    img[src*="https://cdn7.monday.com/images/loader/loader.gif"]
    {
        display: none !important;
    }

    /* mute colors on Monday logo and user avatar */
    .surface-company-logo-image,
    .surface-avatar-menu-component .avatar-photo-button-wrapper .person-bullet-image
    {
        filter: grayscale(100%);
    }

    .application .application-layers .surface
    {
        background-color: #333;
    }

    .surface-navigation-dialog-item-component .item-counter
    {
        background-color: #333;
    }

    .board-wrapper .board-actions-v2 .board-actions-section.subscribers-list-wrapper
    {
        padding-right: 40;
    }

    /* Make text selectable */
    .board-wrapper
    {
        -webkit-user-select: initial;
        -moz-user-select: initial;
        -ms-user-select: initial;
        user-select: initial;
    }

    .application .application-layers .first-level
    {
